import React, { Component } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import $ from 'jquery';

class Lists extends Component {
    constructor(props) {
        super(props)
        this.handleClick = this.handleClick.bind(this);

        this.state = {
            url: 'https://cs371-backend.herokuapp.com/list/',
            movies: [],
            lists: []
        }
    }

    componentDidMount() {
        axios.get("http://cs371-backend.herokuapp.com/list/")
            .then(response => {
                this.setState({
                    lists: response.data
                })
            })
            .catch(function (error) {
                console.log(error)
                alert("error")
            })
    }

    removeMovie(e) {
        e.preventDefault();
        var movieId = e.target.value;
        console.log(movieId);
    }

    handleClick(event) {

    }

    render() {
        //let link;
        return (
            <div>
                {this.state.lists.map((data) => {
                    console.log(data);
                    return (
                        <div id={data.id}>
                            <h1>{data.name}</h1>
                            {data.movies.map((movie) => {
                                return (
                                    <div>
                                        <span>{movie.title}</span>
                                        <Button value={movie.id} onClick={this.removeMovie}>Delete</Button>
                                    </div>
                                )
                            })}
                        </div>
                    )
                })}
            </div>
        );
    }
}
export default Lists;
